    public class RandomUserGenerator
    {
        private static int seed;

        private static ThreadLocal<Random> threadLocal = new ThreadLocal<Random>
            (() => new Random(Interlocked.Increment(ref seed)));

        static RandomUserGenerator()
        {
            seed = Environment.TickCount;
        }

        public static Random Instance { get { return threadLocal.Value; } }

        private static string strGeberator()
        {
            int stringlen = Instance.Next(4, 10);
            int randValue;
            string str = "";
            char letter;
            for (int i = 0; i < stringlen; i++)
            {

                // Generating a random number.
                randValue = Instance.Next(0, 26);

                // Generating random character by converting
                // the random number into character.
                letter = Convert.ToChar(randValue + 65);

                // Appending the letter to string.
                str = str + letter;
            }
            return str;
        }

        protected static string generateEmail()
        {
            // Return random email
            return strGeberator() + "@" + strGeberator() + ".com";
        }

        protected static string generateID()
        {
            // Return random id
            return Instance.Next(100000000, 999999999).ToString();
        }

        protected static string genaratePhone()
        {
            // Return random phone number
            return Instance.Next(100, 999).ToString() + "-" + Instance.Next(1000000, 9999999).ToString();
        }

        protected static string generateGrade()
        {
            // Generate random grade o-100 or 777

            if (Instance.Next(0,12).Equals(0))
            {
                return 777.ToString();
            }
        
            return Instance.Next(0, 100).ToString();
        }

