        public static List<UserData> generateRandomUsers()
        {
            // Run in parralell and crate 100000 users list
            var list = new List<UserData> { };
            for(int i = 0;i < 10000; i ++)
            {
                list.Add(generateUser());
            }
            //Parallel.For(0, 10000, new ParallelOptions { MaxDegreeOfParallelism = 4 }, i =>
