            var g5 = generateGrade();

            return new UserData
            {
                FirstName = generateFirstName(),
                LastName = generateLastName(),
                ID = generateID(),
                Email = generateEmail(),
                PhoneNumber = genaratePhone(),
                Physics = g1,
                Calculus = g2,
                ComputerScience = g3,
